    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String temp = jTextField1.getText() ;
        
        
        Person temp1 = new Person();
        temp1.setLastName(temp);
        familyArray[familyArraySize] = new Family();
        familyArray[familyArraySize].setFather(temp1);
        
        //increase family array size
        familyArraySize++;
        
        jList1.setModel(listModel);
        listModel.insertElementAt(temp, listModel.size());
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        if(selected == -1){
            JOptionPane error = new JOptionPane();
            error.showMessageDialog(rootPane, "Error, Please pick a family!");
        }else{
            Person temp1 = new Person(jTextField5.getText(), jTextField6.getText(),
                                12,
                                jTextField8.getText());
            String temp = temp1.getFirstName() +" "+ temp1.getLastName();

            familyArray[selected].add(temp1);

            jList2.setModel(modelArray[selected]);
            modelArray[selected].insertElementAt(temp,modelArray[selected].size());
        }
    }//GEN-LAST:event_jButton2ActionPerformed

