    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        if(selected >=0 ){
            listModel.remove(selected);
            selected = -1;
        }//if list doesnt have item, print error
        else if(selected == -1) {
            JOptionPane error = new JOptionPane();
            error.showMessageDialog(rootPane, "Error, Please pick a family!");
        }
    }//GEN-LAST:event_jButton3ActionPerformed

    //Remove person
    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        if(selectedJlistPerson >= 0)
        {
            modelArray[selected].remove(selectedJlistPerson);
            selectedJlistPerson = -1;
        } // if list doesnt have item, print error
        else if(selectedJlistPerson == -1) {
            JOptionPane error = new JOptionPane();
            error.showMessageDialog(rootPane, "Error, Please pick a person!");
        }
    }//GEN-LAST:event_jButton4ActionPerformed

